<script>
import anime from 'animejs'

export default {
    data () {
        return {
            persons: []
        }
    },
    mounted () {
        this.persons = [
            this.$refs.Person1,
            this.$refs.Person2,
            this.$refs.Person3,
            this.$refs.Person4,
            this.$refs.Person5,
            this.$refs.Person6,
            this.$refs.Person7,
            this.$refs.Person8,
            this.$refs.Person9,
            this.$refs.Person10,
            this.$refs.Person11,
            this.$refs.Person12,
            this.$refs.Person13,
            this.$refs.Person14,
            this.$refs.Person15,
            this.$refs.Person16,
            this.$refs.Person17,
            this.$refs.Person18,
            this.$refs.Person19,
            this.$refs.Person20,
            this.$refs.Person21,
            this.$refs.Person22,
            this.$refs.Person23,
            this.$refs.Person24,
            this.$refs.Person25,
            this.$refs.Person26,
            this.$refs.Person27,
            this.$refs.Person28,
            this.$refs.Person29,
            this.$refs.Person30,
            this.$refs.Person31,
            this.$refs.Person32,
            this.$refs.Person33,
            this.$refs.Person34,
            this.$refs.Person35,
            this.$refs.Person36
        ]

        const update = () => {
            const i = Math.abs(Math.floor(Math.random() * this.persons.length - 1))
            this.highLightPerson(i)
            setTimeout(() => {
                update()
            }, 500)
        }

        update()
    },
    methods: {
        highLightPerson (index) {
            anime.timeline()
            .add({
                targets: this.persons[index],
                opacity: 1,
                duration: 500,
                easing: 'easeInOutSine'
            }).add({
                targets: this.persons[index],
                opacity: 0,
                duration: 500,
                easing: 'easeInOutSine'
            })
        }
    }
}
</script>

